mapper.select:
SELECT 

FROM rdc_out_header a

WHERE a.id = #{id}

SELECT
FROM rdc_out_header a LEFT JOIN code_data c ON c.out_header_id = a.id
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.order_code LIKE '%'||#{orderCode}||'%' '%'+#{orderCode}+'%' concat('%',#{orderCode},'%')
AND a.bill_status = #{billStatus}
AND a.submit_date BETWEEN #{beginSubmitDate} AND #{endSubmitDate}
AND a.send_name LIKE '%'||#{sendName}||'%' '%'+#{sendName}+'%' concat('%',#{sendName},'%')
AND a.receive_phone LIKE '%'||#{receivePhone}||'%' '%'+#{receivePhone}+'%' concat('%',#{receivePhone},'%')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT
FROM rdc_out_header a LEFT JOIN code_data c ON c.out_header_id = a.id
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.order_code LIKE '%'||#{orderCode}||'%' '%'+#{orderCode}+'%' concat('%',#{orderCode},'%')
AND a.bill_status = 1
AND a.submit_date BETWEEN #{beginSubmitDate} AND #{endSubmitDate}
AND a.send_name LIKE '%'||#{sendName}||'%' '%'+#{sendName}+'%' concat('%',#{sendName},'%')
AND a.receive_phone LIKE '%'||#{receivePhone}||'%' '%'+#{receivePhone}+'%' concat('%',#{receivePhone},'%')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT
FROM rdc_out_header a LEFT JOIN code_data c ON c.out_header_id = a.id
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.order_code LIKE '%'||#{orderCode}||'%' '%'+#{orderCode}+'%' concat('%',#{orderCode},'%')
AND a.bill_status = 0
AND a.submit_date BETWEEN #{beginSubmitDate} AND #{endSubmitDate}
AND a.send_name LIKE '%'||#{sendName}||'%' '%'+#{sendName}+'%' concat('%',#{sendName},'%')
AND a.receive_phone LIKE '%'||#{receivePhone}||'%' '%'+#{receivePhone}+'%' concat('%',#{receivePhone},'%')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM rdc_out_header a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE rdc_out_header SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
