mapper.select:
SELECT 

FROM rdc_warehouse a

WHERE a.id = #{id}

SELECT 

FROM rdc_warehouse a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.code = #{code}


		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')






		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM rdc_warehouse a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE rdc_warehouse SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
